
public class HelloJavaTest_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 개인 신상 정보
		System.out.println("-----------------------");
		System.out.println("개인 신상 정보");
		System.out.println("-----------------------");
		
		// 정보
		// 이름
		String name = "홍길동";
		// 변수 선언
		String name1;
		name1 = "홍길동";
		// 디버깅
		System.out.println("1. 이름 : " + name1);
		
		// 나이
		int age = 100;
		// 변수 선언
		int age1;
		age1 = 100;
		// 디버깅
		System.out.println("2. 나이 : " + age1);
		
		// 성별
		char gen = '남';
		// 변수 선언
		char gen1;
		gen1 = '남';
		// 디버깅
		System.out.println("3. 성별 : " + gen1);
		
		// 몸무게
		float we = 60.5f;
		// 변수 선언
		float we1;
		we1 = 60.5f;
		// 디버깅
		System.out.println("4. 몸무게 : " + we1 + "kg");
		
		// 키
		double he = 178.6;
		// 변수 선언
		double he1;
		he1 = 178.6;
		//디버깅
		System.out.println("5. 키 : " + he1 + "cm");
		
		// 주소
		String ad = "조선 한양 홍대감댁";
		// 변수 선언
		String ad1;
		ad1 = "조선 한양 홍대감댁";
		// 디버깅
		System.out.println("6. 주소 : " + ad1);
		
		System.out.println("-----------------------");
		
	}

}
