package operTest1208;


public class OpTest3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 논리 연산자
		// 제어문(if)에서 많이 활용함
		int num1 = 100, num2 = 2;
		
		// == : 같은가
		// num1과 num2가 같으냐
		System.out.println(" == 연산자 : " + (num1 == num2));
		
		// != : 다른가
		// num1과 num2가 다르냐
		System.out.println(" != 연산자 : " + (num1 != num2));
		
		// > : 보다 크냐
		// num1이 num2보다 크냐
		System.out.println(" > 연산자 : " + (num1 > num2));
		
		// < : 보다 작냐
		// num1이 num2보다 작냐
		System.out.println(" < 연산자 : " + (num1 < num2));
		
		// < : 크거나 같냐
		// num1이 num2보다 크거나 같냐
		System.out.println(" >= 연산자 : " + (num1 >= num2));
		
		// < : 작거나 같냐
		// num1이 num2보다 작거나 같냐
		System.out.println(" <= 연산자 : " + (num1 <= num2));
	}

}
