package test01;

public class T3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 논리 연산자, 제어문(if)에서 많이 활용됨
		// 참이면 true, 거짓이면 false
		int a = 100;
		int b = 250;
		
		// == : 같은가
		System.out.println(a == b);
		
		// != : 다른가
		System.out.println(a != b);
		
		// a > b : a가 b보다 큰가
		System.out.println(a > b);
		
		// a < b : a가 b보다 작은가
		System.out.println(a < b);
		
		// a >= b : a가 b보다 크거나 같은가
		System.out.println(a >= b);
		
		// a <= b : a가 b보다 작거나 같은가
		System.out.println(a <= b);
	}

}
