package test01;

public class T6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(2.9+1.1); // 2.9+1.1=4.0
		System.out.println((int)2.9+1.1); // 2+1.1=3.1
		System.out.println((int)(2.9+1.1)); // (int)(2.9+1.1)=(int)(4.0)=4
		System.out.println((int)2.9+(int)1.1); // 2+1=3
	}

}
