
public class For_01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*  for(초기값; 조건; 증가또는감소){
		*		실행값;		
		*	}
		*/
		String str[] = {"하나", "둘", "셋"};
		for(int i=0; i<3; i++) { // i++ : i에 1 더하기
			System.out.println("로이드");		
		}
		System.out.println("안드");
		
		for(int i=0; i<str.length; i++) {// legnth : 길이
			System.out.println("오케이");
		}
	}

}
